import { useState } from 'react';
import './sidebar.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

const [clicked, setClicked] = useState();

function Item(props) {
    

    const handleOnClick = () => {
        console.log(clicked);
        if (clicked){
            console.log(true);
            const itemclicked = document.getElementById(clicked);
            itemclicked.classList.remove("Item_active");
            const itemClick = document.getElementById(props.id);
            itemClick.classList.add("Item_active");
            setClicked(props.id);
        }else{
            console.log(false);
            const item = document.getElementById(props.id);
            item.classList.add("Item_active");
            setClicked(props.id);
        }
    }
    
    return (
        <div className="Item" id={props.id} onClick={handleOnClick}>
            <div className="iconItem" ><FontAwesomeIcon icon={props.icon} /> </div>
            <label className="nameItem">{props.title}</label>
        </div>
    );
}

export default Item;
