import { useState } from 'react';
import './sidebar.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

function Item(props) {
    const [clicked, setClicked] = useState();

    const handleOnClick = () => {
        setChecked(props.id)
        const item = document.getElementById(props.id);
        item.classList.add("Item_active");
    }
    
    return (
        <div className="Item" id={props.id} onClick={handleOnClick}>
            <div className="iconItem" ><FontAwesomeIcon icon={props.icon} /> </div>
            <label className="nameItem">{props.title}</label>
        </div>
    );
}

export default Item;
