import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons'
import ItemListChat from './itemlistchat';
import { createContext, useState } from 'react';

export const ListChatContext = createContext();

function ListChat() {
    const getdata = localStorage.getItem('chatdata');
    const chatdata = JSON.parse(getdata);
    const [chooseUser, setChooseUser] = useState(1);

    const handleClicked = (idchoose) => {
        setChooseUser(idchoose)
    }

    return (

        <div className='listchat'>
            <ListChatContext.Provider value={chooseUser}>
                <div className="search">
                    <FontAwesomeIcon icon={faMagnifyingGlass} />
                    <input type="text" placeholder='Search' />
                </div>
                <div className='allitemlistchat'>
                    {chatdata.map(data => (
                        <ItemListChat dataItem={data} onChoose={handleClicked} />
                    ))}
                </div>
            </ListChatContext.Provider>
        </div>

    );
}

export default ListChat;
