import { useState, useLayoutEffect, useEffect,  } from "react";

function Message() {
    const getdata = localStorage.getItem('chatdata');
    const chatdata = JSON.parse(getdata);
    let choosen = localStorage.getItem('chooseUser');
    const [choose, setChoose] = useState(choosen);
    const dataChoose = chatdata[choosen-1];
    const dataMessage = dataChoose.message;
    console.log(dataMessage);

    const messageboxRef = useRef(null);

    useLayoutEffect(() => {
      messageboxRef.current.scrollTop = messageboxRef.current.scrollHeight;
    }, []);
  
    useEffect(() => {
      messageboxRef.current.scrollTop = messageboxRef.current.scrollHeight;
    }, [dataMessage]);

    return (
        <div className='message'>
            <div className='infousermessage'>
                <p className='namemessage'>Quang Vinh</p>
                <p className='nationuser'>From: VietNam</p>
            </div>
            <div className="messagebox" ref={messageboxRef}>
                {dataMessage.map(data => (
                    <div className={`linemessage ${data.people}`}>
                        {data.chat}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Message;
