import { useState } from 'react';
import './sidebar.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

function Item(props) {
    const [clicked, setClicked] = useState();
    const [newclick, setNewClick] = useState();

    const handleOnClick = () => {
        setNewClick()

        if(clicked == props.id){
            setClicked(props.id);
            item.classList.add("Item_active");
        }else
        {
            const itemclicked = document.getElementById(clicked);
            itemclicked.classList.remove("Item_active");
            item.classList.add("Item_active");
            setClicked(props.id);
        } 
    }
    
    return (
        <div className="Item" id={props.id} onClick={handleOnClick}>
            <div className="iconItem" ><FontAwesomeIcon icon={props.icon} /> </div>
            <label className="nameItem">{props.title}</label>
        </div>
    );
}

export default Item;
